import React from 'react'
import Link from 'next/link'
import {useDispatch} from 'react-redux';

type Props = {
    lists: string[]
}

const Index = ({ lists }: Props) => {
    return (
        <>
            {lists && lists.map(list => (
                <>
                    <Link href={{pathname:'noticeDetail', query: {id: list.NTC_ID}}}><li props={list} key={list.NTC_ID} onClick={() => dispatch(getNtc(list.NTC_ID))}>{list.NTC_TITLE}</li></Link>
                </>
            ))}
        </>
    )
}
Index.getInitialProps = async () => {
    const response = await axios.get('http://15.165.161.162:8086/api/web/notice/NoticeList?PAGE=1');
    return {
        lists : response.data.data[0].list
    }
}


export default Index
